			this.state.STARTING_TODOS.unshift(STARTING[this.number])
			this.number=this.number+1
			console.log(this.state.STARTING_TODOS)
			this.setState({TODOS:this.state.STARTING_TODOS})
		}
	}
	render(){
	function handleClick (props){
