import React,{Component} from 'react'
import './App.css';
import Item from './Item'
class App extends React.Component{
	state = {STARTING_TODOS:[
	  { id: 1, description: 'Eat Breakfast', completed: true },
	  { id: 2, description: 'Deploy to Heroku', completed: false },
	  { id: 3, description: 'Check Email', completed: true },
	  { id: 4, description: 'Eat Lunch', completed: false }
	]}

	render(){
		const {STARTING_TODOS}=this.state
		function activateLasers(e) {
		  
		   const TOobject=[{ id: 1, description: 'Eat Breakfast', completed: true }]
		   const newTODOs=[TOobject,...STARTING_TODOS]
		   this.setState({STARTING_TODOS:newTODOs})
		}
		return(
		  <div className="App">
			 <h1>If you can dream it,you can TODO it!</h1>
			 <h2>There are no tasks remaining! You shuold add one</h2>
			 <ul id="todos">
			  {
			 				 STARTING_TODOS.map((to,index)=>{
			 						 return <Item key={to.id} {...to}/>
			 				 })
			  }
			 </ul>
			 <a href="#" onClick={activateLasers} >Add task</a> 
		  </div>
		)
	}
}
export default App
