import React,{Component} from 'react'
import './App.css';
import Item from './Item'
class App extends React.Component{
	constructor(props) {
    super(props);
    this.state = { STARTING_TODOS :[
  { id: 1, description: 'Eat Breakfast', completed: true },
  { id: 2, description: 'Deploy to Heroku', completed: false },
  { id: 3, description: 'Check Email', completed: true },
  { id: 4, description: 'Eat Lunch', completed: false },
  ]};
    this.handleClick = this.handleClick.bind(this);//手动绑定
	}

	render(){
		function handleClick (props){
			const {show} = props;
			if (!show){
			  return null;  // 提前返回null，不渲染子组件的内容
			}
			return (<p> 这是子组件的内容 </p> )
		}
		const {STARTING_TODOS } =this.state
		return(
		  <div className="App">
		         <h1>If you can dream it,you can TODO it!</h1>
				 <h2>There are no tasks remaining! You shuold add one</h2>
				 <ul id="todos">
				     {
						 STARTING_TODOS.map((to,index)=>{
								 return <Item key={to.id} {...to}/>
						 })
					 }
				 </ul>
			<a href="#"  onClick={this.handleClick(this)}>Add task</a>
			</div>
		)
	}
}
export default App
